import React, { useState, useEffect, useCallback } from 'react';
import { BrowserRouter } from 'react-router-dom';
import { apiGetUserProfile } from './api/auth'; // Asegúrate que la ruta sea correcta
import Notification from './components/Notification'; // Asegúrate que la ruta sea correcta
import Navbar from './components/Navbar'; // Asegúrate que la ruta sea correcta
import AppRouter from './routes/AppRouter'; // Asegúrate que la ruta sea correcta

const appId = 'GestorProcesos-Frontend';

function App() {
    const [user, setUser] = useState(null);
    const [isLoadingUser, setIsLoadingUser] = useState(true);
    const [currentPage, setCurrentPage] = useState('login'); // O la página inicial que prefieras
    const [notification, setNotificationMessage] = useState('');
    const [notificationType, setNotificationType] = useState('success');

    const setNotification = useCallback((message, type) => {
        setNotificationMessage(message);
        setNotificationType(type);
        if (window.notificationTimeout) clearTimeout(window.notificationTimeout);
        window.notificationTimeout = setTimeout(() => setNotificationMessage(''), 5000);
    }, []);

    useEffect(() => {
        const checkAuthStatus = async () => {
            setIsLoadingUser(true);
            const token = localStorage.getItem('authToken');

            if (token) {
                try {
                    // Llamada a apiGetUserProfile con el token recuperado
                    const userProfile = await apiGetUserProfile(token);
                    // Si la API devuelve { usuario: {...} }, ajusta aquí:
                    // setUser(userProfile.usuario);
                    // Si la API devuelve directamente el objeto de usuario:
                    setUser(userProfile); // Ajusta esto según la estructura real de userProfile
                    setCurrentPage('dashboard'); // O la página a la que va tras login
                } catch (error) {
                    localStorage.removeItem('authToken');
                    localStorage.removeItem('userId'); // También elimina userId si lo guardas
                    setUser(null);
                    setCurrentPage('login');
                    if (error.message.includes('Sesión inválida')) {
                        setNotification(error.message, 'error');
                    }
                }
            } else {
                setUser(null);
                // Si no hay token, y no estamos en login/register, redirigir a login
                // Esto puede necesitar ajustarse según tu lógica de enrutamiento
                if (currentPage !== 'register' && currentPage !== 'login') {
                     setCurrentPage('login');
                }
            }
            setIsLoadingUser(false);
        };
        checkAuthStatus();
    }, [setNotification, currentPage]); // currentPage como dependencia puede causar re-chequeos frecuentes, evalúa si es necesario.

    const handleLoginSuccess = (userData) => {
        // userData podría ser el objeto que devuelve apiLogin.
        // Si apiLogin devuelve { usuario: {...} }, asegúrate de pasar el objeto correcto.
        setUser(userData);
        setCurrentPage('dashboard');
        // No necesitas llamar a apiGetUserProfile aquí si apiLogin ya te da los datos del usuario
        // o si prefieres que checkAuthStatus (que se podría disparar por cambio de estado) lo haga.
        // Sin embargo, es común que tras el login, el backend ya devuelva los datos del usuario.
        // Si apiLogin guarda el token, y luego necesitas los datos del perfil completos, podrías
        // llamar a una función que use apiGetUserProfile, o confiar en el useEffect.
    };

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        localStorage.removeItem('userId');
        setUser(null);
        setCurrentPage('login');
        // Podrías querer usar navigate de react-router-dom aquí si estás en un contexto de ruta
    };

    const handleRegisterSuccess = () => {
        setCurrentPage('login');
        setNotification('¡Registro exitoso! Por favor, inicia sesión.', 'success');
    };

    if (isLoadingUser) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-slate-900">
                <div className="text-center">
                    <svg className="animate-spin h-12 w-12 text-sky-400 mx-auto mb-4" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    <p className="text-xl text-sky-300">Verificando sesión...</p>
                </div>
            </div>
        );
    }

    return (
        <BrowserRouter>
            <div className="min-h-screen bg-slate-900 text-white flex flex-col">
                <Notification message={notification} type={notificationType} onClose={() => setNotificationMessage('')} />
                {user && (
                    <Navbar
                        onLogout={handleLogout}
                        userEmail={user.correo || user.email} // Ajusta según el campo de email en tu objeto user
                        onNavigate={setCurrentPage} // Asumiendo que Navbar maneja esta lógica de "página actual"
                        currentPage={currentPage}
                        setNotification={setNotification}
                    />
                )}
                <main className="flex-grow">
                    <AppRouter // AppRouter debe manejar qué componente mostrar basado en `user` y `currentPage`
                        user={user}
                        onLoginSuccess={handleLoginSuccess}
                        onRegisterSuccess={handleRegisterSuccess}
                        setNotification={setNotification}
                        handleLogout={handleLogout} // Pasa handleLogout si algún componente hijo necesita desloguear
                        // Props adicionales que AppRouter o sus hijos puedan necesitar
                        // como setCurrentPage si quieres que los hijos cambien la "página" de App.js
                        currentPage={currentPage}
                        setCurrentPage={setCurrentPage}
                    />
                </main>
                <footer className="bg-slate-800 text-center p-4 text-sm text-slate-500">
                    © {new Date().getFullYear()} Mi Aplicación Moderna. Todos los derechos reservados. AppID: {appId}
                </footer>
            </div>
        </BrowserRouter>
    );
}

export default App;
