import React, { useEffect, useState } from 'react';
import { apiConsultarSolicitudes } from '../api/auth';

const SolicitudesPage = ({ setNotification }) => {
    const [solicitudes, setSolicitudes] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchSolicitudes = async () => {
            try {
                const token = localStorage.getItem('authToken');
                const data = await apiConsultarSolicitudes(token);
                setSolicitudes(data.solicitudes || []);
            } catch (e) {
                setNotification('Error al cargar solicitudes', 'error');
            }
            setLoading(false);
        };
        fetchSolicitudes();
    }, [setNotification]);

    if (loading) return <div className="p-8">Cargando solicitudes...</div>;

    return (
        <div className="p-8">
            <h2 className="text-2xl font-bold mb-4 text-sky-400">Solicitudes</h2>
            {solicitudes.length === 0 ? (
                <div className="text-gray-400">No hay solicitudes registradas.</div>
            ) : (
                <ul className="space-y-2">
                    {solicitudes.map((s) => (
                        <li key={s._id || s.idSolicitud || s.folio} className="bg-slate-700 p-4 rounded">
                            <div>
                                <span className="font-semibold">Folio:</span> {s.folio || 'Sin folio'}
                            </div>
                            <div>
                                <span className="font-semibold">Título:</span> {s.titulo || s.nombre || 'Sin título'}
                            </div>
                            <div>
                                <span className="font-semibold">Estatus:</span> {s.estatus || 'Desconocido'}
                            </div>
                            {/* Agrega aquí más campos según tu modelo */}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default SolicitudesPage;
