import React, { useState, useEffect, useCallback } from 'react';
import { LogIn, LogOut, UserPlus, LayoutDashboard, Box, Package, Settings, AlertTriangle, CheckCircle, Eye, EyeOff } from 'lucide-react';

// Identificador para la aplicación frontend, puedes cambiarlo si es necesario.
const appId = 'GestorProcesos-Frontend'; 

// --- API Helper Functions ---
const API_BASE_URL = '/api'; // Ajusta esto si tu backend está en un dominio/puerto diferente

async function apiLogin(email, password) {
    const response = await fetch(`${API_BASE_URL}/auth/token`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded', // FastAPI OAuth2PasswordRequestForm espera esto
        },
        // FastAPI espera 'username' y 'password' en el body para OAuth2PasswordRequestForm
        body: new URLSearchParams({
            username: email, 
            password: password,
        }),
    });
    if (!response.ok) {
        const errorData = await response.json().catch(() => ({ detail: 'Error de red o respuesta no JSON' }));
        throw new Error(errorData.detail || `Error ${response.status}`);
    }
    return response.json(); // Espera { access_token: "...", token_type: "bearer", user_email: "..." }
}

async function apiRegister(email, password) {
    const response = await fetch(`${API_BASE_URL}/users/register`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
    });
    if (!response.ok) {
        const errorData = await response.json().catch(() => ({ detail: 'Error de red o respuesta no JSON' }));
        throw new Error(errorData.detail || `Error ${response.status}`);
    }
    return response.json(); // Espera { id: "...", email: "...", message: "..." }
}

// Opcional: Una función para obtener datos del usuario si tienes un endpoint /users/me
async function apiGetUserProfile(token) {
    const response = await fetch(`${API_BASE_URL}/users/me`, {
        headers: {
            'Authorization': `Bearer ${token}`,
        },
    });
    if (!response.ok) {
        // Si el token es inválido o expiró, el backend podría devolver 401
        if (response.status === 401) {
            localStorage.removeItem('authToken'); // Limpiar token inválido
            throw new Error('Sesión inválida o expirada.');
        }
        const errorData = await response.json().catch(() => ({ detail: 'Error de red o respuesta no JSON' }));
        throw new Error(errorData.detail || `Error ${response.status}`);
    }
    return response.json(); // Espera datos del perfil del usuario, ej: { email: "...", id: "..." }
}


// Componente para mensajes de notificación
const Notification = ({ message, type, onClose }) => {
    if (!message) return null;
    const bgColor = type === 'error' ? 'bg-red-500' : 'bg-green-500';
    const Icon = type === 'error' ? AlertTriangle : CheckCircle;

    return (
        <div className={`fixed top-5 right-5 ${bgColor} text-white p-4 rounded-lg shadow-lg flex items-center z-50 animate-slideIn`}>
            <Icon size={24} className="mr-3" />
            <span>{message}</span>
            <button onClick={onClose} className="ml-4 text-xl font-bold">&times;</button>
        </div>
    );
};
// Agrega animación simple para notificaciones
const styles = `
.animate-slideIn {
  animation: slideInFromRight 0.5s ease-out forwards;
}
@keyframes slideInFromRight {
  0% { transform: translateX(100%); opacity: 0; }
  100% { transform: translateX(0); opacity: 1; }
}
.animate-fadeIn {
  animation: fadeIn 0.5s ease-out forwards;
}
@keyframes fadeIn {
  0% { opacity: 0; transform: translateY(10px); }
  100% { opacity: 1; transform: translateY(0); }
}
`;
// Inyectar estilos al head una sola vez
if (!document.getElementById('custom-app-animations')) {
    const styleSheet = document.createElement("style");
    styleSheet.type = "text/css";
    styleSheet.id = 'custom-app-animations';
    styleSheet.innerText = styles;
    document.head.appendChild(styleSheet);
}


// Componente para la página de Login
const LoginPage = ({ onLoginSuccess, onSwitchToRegister, setNotification }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    const handleLogin = async (e) => {
        e.preventDefault();
        if (!email || !password) {
            setNotification('Por favor, ingresa email y contraseña.', 'error');
            return;
        }
        setIsLoading(true);
        try {
            const data = await apiLogin(email, password);
            localStorage.setItem('authToken', data.access_token);
            // Asumimos que apiLogin devuelve user_email o podemos obtenerlo de /users/me
            // Si tu endpoint /auth/token no devuelve user_email, necesitarás llamar a apiGetUserProfile aquí
            const userProfile = data.user_email 
                ? { email: data.user_email, id: data.user_id } // Asume que tu backend devuelve user_id también
                : await apiGetUserProfile(data.access_token);
            onLoginSuccess(userProfile); 
            setNotification('Inicio de sesión exitoso.', 'success');
        } catch (error) {
            console.error("Error al iniciar sesión:", error);
            setNotification(error.message || 'Error al iniciar sesión. Inténtalo de nuevo.', 'error');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-900 to-slate-700 p-4">
            <div className="bg-slate-800 p-8 md:p-12 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-500 hover:scale-105">
                <h2 className="text-4xl font-bold text-center text-sky-400 mb-8">Iniciar Sesión</h2>
                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-sky-300 mb-1" htmlFor="email">
                            Correo Electrónico
                        </label>
                        <input
                            id="email"
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full px-4 py-3 bg-slate-700 text-white border border-slate-600 rounded-lg focus:ring-2 focus:ring-sky-500 focus:border-sky-500 outline-none transition-colors"
                            placeholder="tu@email.com"
                            required
                            autoComplete="email"
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-sky-300 mb-1" htmlFor="password">
                            Contraseña
                        </label>
                        <div className="relative">
                            <input
                                id="password"
                                type={showPassword ? "text" : "password"}
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full px-4 py-3 bg-slate-700 text-white border border-slate-600 rounded-lg focus:ring-2 focus:ring-sky-500 focus:border-sky-500 outline-none transition-colors"
                                placeholder="••••••••"
                                required
                                autoComplete="current-password"
                            />
                            <button 
                                type="button" 
                                onClick={() => setShowPassword(!showPassword)} 
                                className="absolute inset-y-0 right-0 px-3 flex items-center text-sky-400 hover:text-sky-300"
                                aria-label={showPassword ? "Ocultar contraseña" : "Mostrar contraseña"}
                            >
                                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                            </button>
                        </div>
                    </div>
                    <button
                        type="submit"
                        disabled={isLoading}
                        className="w-full bg-sky-500 hover:bg-sky-600 text-white font-semibold py-3 px-4 rounded-lg shadow-md hover:shadow-lg transition-all duration-300 ease-in-out transform hover:-translate-y-0.5 disabled:opacity-50 flex items-center justify-center"
                    >
                        {isLoading ? (
                            <svg className="animate-spin h-5 w-5 mr-3 text-white" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                            </svg>
                        ) : <LogIn size={20} className="mr-2" />}
                        {isLoading ? 'Ingresando...' : 'Ingresar'}
                    </button>
                </form>
                <p className="text-center text-sm text-slate-400 mt-8">
                    ¿No tienes una cuenta?{' '}
                    <button onClick={onSwitchToRegister} className="font-medium text-sky-400 hover:text-sky-300">
                        Regístrate aquí
