                </p>
            </div>
        </div>
    );
};

// Componente para la página de Registro
const RegisterPage = ({ onRegisterSuccess, onSwitchToLogin, setNotification }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);

    const handleRegister = async (e) => {
        e.preventDefault();
        if (password !== confirmPassword) {
            setNotification('Las contraseñas no coinciden.', 'error');
            return;
        }
        if (!email || !password) {
            setNotification('Por favor, completa todos los campos.', 'error');
            return;
        }
        // Validación básica de contraseña (ej. mínimo 6 caracteres)
        if (password.length < 6) {
            setNotification('La contraseña debe tener al menos 6 caracteres.', 'error');
            return;
        }
        setIsLoading(true);
        try {
            const data = await apiRegister(email, password);
            setNotification(data.message || 'Registro exitoso. Ahora puedes iniciar sesión.', 'success');
            onRegisterSuccess(); // Podría redirigir a login o mostrar mensaje
        } catch (error) {
            console.error("Error al registrar:", error);
            setNotification(error.message || 'Error durante el registro. Inténtalo de nuevo.', 'error');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-900 to-slate-700 p-4">
            <div className="bg-slate-800 p-8 md:p-12 rounded-xl shadow-2xl w-full max-w-md transform transition-all duration-500 hover:scale-105">
                <h2 className="text-4xl font-bold text-center text-sky-400 mb-8">Crear Cuenta</h2>
                <form onSubmit={handleRegister} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-sky-300 mb-1" htmlFor="email-register">
                            Correo Electrónico
                        </label>
                        <input
                            id="email-register"
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full px-4 py-3 bg-slate-700 text-white border border-slate-600 rounded-lg focus:ring-2 focus:ring-sky-500 focus:border-sky-500 outline-none transition-colors"
                            placeholder="tu@email.com"
                            required
                            autoComplete="email"
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-sky-300 mb-1" htmlFor="password-register">
                            Contraseña
                        </label>
                         <div className="relative">
                            <input
                                id="password-register"
                                type={showPassword ? "text" : "password"}
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="w-full px-4 py-3 bg-slate-700 text-white border border-slate-600 rounded-lg focus:ring-2 focus:ring-sky-500 focus:border-sky-500 outline-none transition-colors"
                                placeholder="Mínimo 6 caracteres"
                                required
                                autoComplete="new-password"
                            />
                            <button 
                                type="button" 
                                onClick={() => setShowPassword(!showPassword)} 
                                className="absolute inset-y-0 right-0 px-3 flex items-center text-sky-400 hover:text-sky-300"
                                aria-label={showPassword ? "Ocultar contraseña" : "Mostrar contraseña"}
                            >
                                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                            </button>
                        </div>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-sky-300 mb-1" htmlFor="confirm-password">
                            Confirmar Contraseña
                        </label>
                        <div className="relative">
                            <input
                                id="confirm-password"
                                type={showConfirmPassword ? "text" : "password"}
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                                className="w-full px-4 py-3 bg-slate-700 text-white border border-slate-600 rounded-lg focus:ring-2 focus:ring-sky-500 focus:border-sky-500 outline-none transition-colors"
                                placeholder="Repite tu contraseña"
                                required
                                autoComplete="new-password"
                            />
                            <button 
                                type="button" 
                                onClick={() => setShowConfirmPassword(!showConfirmPassword)} 
                                className="absolute inset-y-0 right-0 px-3 flex items-center text-sky-400 hover:text-sky-300"
                                aria-label={showConfirmPassword ? "Ocultar confirmación de contraseña" : "Mostrar confirmación de contraseña"}
                            >
                                {showConfirmPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                            </button>
                        </div>
                    </div>
                    <button
                        type="submit"
                        disabled={isLoading}
                        className="w-full bg-teal-500 hover:bg-teal-600 text-white font-semibold py-3 px-4 rounded-lg shadow-md hover:shadow-lg transition-all duration-300 ease-in-out transform hover:-translate-y-0.5 disabled:opacity-50 flex items-center justify-center"
                    >
                         {isLoading ? (
                            <svg className="animate-spin h-5 w-5 mr-3 text-white" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                            </svg>
                        ) : <UserPlus size={20} className="mr-2" />}
                        {isLoading ? 'Registrando...' : 'Registrar'}
                    </button>
                </form>
                <p className="text-center text-sm text-slate-400 mt-8">
                    ¿Ya tienes una cuenta?{' '}
                    <button onClick={onSwitchToLogin} className="font-medium text-sky-400 hover:text-sky-300">
                        Inicia sesión aquí
                    </button>
                </p>
            </div>
        </div>
    );
};

