// Componente Principal App
const App = () => {
    const [user, setUser] = useState(null); // Almacenará { email: '...', id: '...' } u otros datos del usuario
    const [isLoadingUser, setIsLoadingUser] = useState(true); // Para la carga inicial del perfil
    const [currentPage, setCurrentPage] = useState('login'); // 'login', 'register', 'dashboard', ...
    const [notification, setNotificationMessage] = useState('');
    const [notificationType, setNotificationType] = useState('success');

    const setNotification = useCallback((message, type) => {
        setNotificationMessage(message);
        setNotificationType(type);
        // Clear previous timeout if exists
        if (window.notificationTimeout) {
            clearTimeout(window.notificationTimeout);
        }
        window.notificationTimeout = setTimeout(() => {
            setNotificationMessage('');
        }, 5000);
    }, []);

    // Verificar token al cargar la app
    useEffect(() => {
        const checkAuthStatus = async () => {
            setIsLoadingUser(true); // Asegurarse de mostrar carga al verificar
            const token = localStorage.getItem('authToken');
            if (token) {
                try {
                    const userProfile = await apiGetUserProfile(token); 
                    setUser(userProfile); // userProfile debería tener { email: '...', id: '...' }
                    setCurrentPage('dashboard');
                } catch (error) {
                    console.warn("Error al validar token o cargar perfil:", error.message);
                    localStorage.removeItem('authToken'); 
                    setUser(null);
                    setCurrentPage('login');
                    if (error.message.includes('Sesión inválida')) {
                        setNotification(error.message, 'error');
                    }
                }
            } else {
                setUser(null);
                if (currentPage !== 'register' && currentPage !== 'login') { // Evitar bucle si ya está en login/register
                    setCurrentPage('login');
                }
            }
            setIsLoadingUser(false);
        };
        checkAuthStatus();
    // eslint-disable-next-line react-hooks/exhaustive-deps 
    }, [setNotification]); // No incluir currentPage aquí para evitar re-chequeos innecesarios en cada navegación

    const handleLoginSuccess = (userData) => {
        setUser(userData); // userData debe ser { email: '...', id: '...' }
        setCurrentPage('dashboard');
    };

    const handleLogout = () => {
        setUser(null);
        setCurrentPage('login');
    };

    const handleRegisterSuccess = () => {
        setCurrentPage('login'); 
    };

    const handleNavigate = (page) => {
        setCurrentPage(page);
    };

    if (isLoadingUser) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-slate-900">
                <div className="text-center">
                    <svg className="animate-spin h-12 w-12 text-sky-400 mx-auto mb-4" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    <p className="text-xl text-sky-300">Verificando sesión...</p>
                </div>
            </div>
        );
    }

    const renderPage = () => {
        if (!user) { 
            if (currentPage === 'register') {
                return <RegisterPage 
                            onRegisterSuccess={handleRegisterSuccess}
                            onSwitchToLogin={() => setCurrentPage('login')}
                            setNotification={setNotification} 
                        />;
            }
            return <LoginPage 
                        onLoginSuccess={handleLoginSuccess}
                        onSwitchToRegister={() => setCurrentPage('register')}
                        setNotification={setNotification} 
                    />;
        }

        // Si el usuario está autenticado
        switch (currentPage) {
            case 'dashboard':
                return <DashboardPage userName={user.email} userId={user.id} />; 
            case 'module1':
                return <ModulePage title="Módulo 1: Gestión de Proyectos" icon={Box} />;
            case 'module2':
                return <ModulePage title="Módulo 2: Análisis de Datos" icon={Package} />;
            case 'module3':
                return <ModulePage title="Módulo 3: Configuración Avanzada" icon={Settings} />;
            default:
                setCurrentPage('dashboard'); 
                return <DashboardPage userName={user.email} userId={user.id}/>;
        }
    };

    return (
        <div className="min-h-screen bg-slate-900 text-white flex flex-col">
            <Notification message={notification} type={notificationType} onClose={() => setNotificationMessage('')} />
            {user && <Navbar 
                        onLogout={handleLogout} 
                        userEmail={user.email} 
                        onNavigate={handleNavigate}
                        currentPage={currentPage}
                        setNotification={setNotification}
                    />}
            <main className="flex-grow">
                {renderPage()}
            </main>
            <footer className="bg-slate-800 text-center p-4 text-sm text-slate-500">
                © {new Date().getFullYear()} Mi Aplicación Moderna. Todos los derechos reservados. AppID: {appId}
            </footer>
        </div>
    );
};

export default App;
