const API_BASE_URL = 'http://localhost:8001'; // Usar la URL base real del backend

// Login: autenticación HTTP Basic, obtiene usuario si las credenciales son correctas
export async function apiLogin(email, password) {
    const response = await fetch(`${API_BASE_URL}/usuarios/login`, { //
        method: 'GET', //
        headers: {
            'Authorization': 'Basic ' + btoa(`${email}:${password}`), //
        },
    });

    let user;
    try {
        user = await response.json(); //
    } catch {
        user = {}; //
    }

    // Opcional: puedes dejar este console.log si te es útil durante el desarrollo
    // console.log('Respuesta del backend en apiLogin:', user);

    if (!response.ok) { //
        throw new Error(user.mensaje || user.detail || `Error ${response.status}`); //
    }
    // Ahora valida el campo correcto
    if (!user || !user.usuario || !user.usuario._id) { //
        throw new Error('Credenciales incorrectas'); //
    }
    // Guarda el userId para futuras consultas
    localStorage.setItem('userId', user.usuario._id); //
    // Guarda el token (email:password en base64)
    localStorage.setItem('authToken', btoa(`${email}:${password}`)); //
    return user.usuario; //
}

// Registro: crea usuario nuevo
export async function apiRegister(email, password) {
    const response = await fetch(`${API_BASE_URL}/usuarios/`, { //
        method: 'POST', //
        headers: { 'Content-Type': 'application/json' }, //
        body: JSON.stringify({
            nombre: email.split('@')[0], //
            correo: email, //
            password: password, //
            estatus: true, //
            rol: 'general' //
        }),
    });
    if (!response.ok) { //
        const errorData = await response.json().catch(() => ({ detail: 'Error de red o respuesta no JSON' })); //
        throw new Error(errorData.detail || `Error ${response.status}`); //
    }
    return response.json(); //
}

// Perfil: obtiene datos del usuario autenticado
// Esta función ESPERA que se le PASE un token válido.
export async function apiGetUserProfile(token) {
    // Se eliminó el console.log('Token received by apiGetUserProfile:', token);
    // porque el problema se resuelve en el código que LLAMA a esta función.

    const userId = localStorage.getItem('userId'); //
    if (!userId) { //
        // Se eliminó el console.error para mantener el código más limpio para producción.
        throw new Error('No hay usuario autenticado'); //
    }

    const response = await fetch(`${API_BASE_URL}/usuarios/${userId}/`, { //
        headers: {
            'Authorization': 'Basic ' + token, // Correcto: usa el token pasado como argumento
        },
    });

    if (!response.ok) { //
        if (response.status === 401) { //
            localStorage.removeItem('authToken'); //
            localStorage.removeItem('userId'); //
            throw new Error('Sesión inválida o expirada.'); //
        }
        const errorData = await response.json().catch(() => ({ detail: 'Error de red o respuesta no JSON' })); //
        throw new Error(errorData.detail || `Error ${response.status}`); //
    }
    return response.json(); //
}
